import React from 'react';


class PlayButton extends React.Component {
    render() {
        return <button className="ui button play-button"><div className="arrow-right"></div></button>; 
    }
}

class Empty extends React.Component {
    render() { 
        const spanStyle = {
            display:'none'
        }
        return <span style={spanStyle}></span>;
    }
}





class Mediaitem extends React.Component { 

    constructor(props){
        super(props);
        this.downloadMedia = this.downloadMedia.bind(this);
        this.copyToClipboard = this.copyToClipboard.bind(this);     
        this.copy = this.copy.bind(this);   
        this.clipBoard = React.createRef();
        this.state = {
            isVideo: false,
            linkUrl:'',
            tvalue:''

        
        };

    }

    componentDidMount(){
       //document.getElementById("copy").addEventListener("click", copy);
    }

    downloadMedia(){
        const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const url = this.props.download_url;
        this.setState.linkUrl = url;
        console.log(url);

        fetch(proxyurl + url, {
            method: 'GET',
            headers: {
                'Content-Type': this.props.media_type,
                'Access-Control-Allow-Origin':'*'
            },
        })
        .then((response) => response.blob())
        .then((blob) => {
            alert(this.props.media_type);
            const link = document.getElementById('forDownload');
            link.setAttribute(
            'download',
            `FileName.pdf`,
            );

            // Start download
            link.click();

        });
    }

    copyToClipboard() {
        const cel = document.getElementById("forClipboard");
        cel.addEventListener("click", this.copyToClipboard);
        const el = this.clipBoard.current;
        el.value = this.props.tracking_link;
        el.setAttribute("readonly", "");
        //el.style.position = "absolute";
        //el.style.left = "-9999px";

        el.select();
        //el.setSelectionRange(0, 99999);
        document.execCommand("copy");
        alert("Copied the text: " + el.value);
    }
    copy() {
        var copyText = document.getElementById("forClipboard");
        copyText.value = this.props.tracking_link;
        //alert("Copied the text: " + copyText.value);
        copyText.select();
        try {
            // Now that we've selected the anchor text, execute the copy command
            console.log('about to try');
            var successful = document.execCommand('copy');
            var msg = successful ? 'successful' : 'unsuccessful';
            console.log('Copy  command was ' + msg);
          } catch(err) {
            console.log('Oops, unable to copy');
          }
      }

    render() {      
        
        let button;
        if(this.props.media_type.toString() === "video"){
            this.setState.isVideo = true;
            button = <PlayButton />        }
        else {
            this.setState.isVideo = false;
            button = <Empty />
        }

        const invisibility = {
            visibility:'hidden'
        }

      return (
          <div className="item">    

                <img src={this.props.cover_photo_url} className="img-responsive item-image" alt="..." width="70" height="120"></img>
                <div className="media-buttons ui icon buttons">
                    <button className="ui button no-play" id="copy" onClick={this.copy}><i className="fas fa-link" aria-hidden="true"></i></button>
                    <button className="ui button no-play" onClick={this.downloadMedia.bind(this.props.download_url, this.props.media_type)}><i className="fas fa-download" aria-hidden="true"></i></button>                   
                    { button }
                </div>
                <a href={this.state.linkUrl} id="forDownload" style={invisibility}>Check</a> 
                <textarea id="forClipboard" className="copy" onChange="return false"  style={invisibility} ref={this.clipBoard}>Check</textarea> 
          </div>
        
     
        )
    }
  }
  export default Mediaitem;
