import React from 'react';


class PlayButton extends React.Component {
    render() {
        return <button className="ui button play-button"><div className="arrow-right"></div></button>; 
    }
}

class Empty extends React.Component {
    render() { 
        const spanStyle = {
            display:'none'
        }
        return <span style={spanStyle}></span>;
    }
}





class Mediaitem extends React.Component { 

    constructor(props){
        super(props);
        this.downloadMedia = this.downloadMedia.bind(this);
        this.copyToClipboard = this.copyToClipboard.bind(this);
        this.state = {
            isVideo: false,
            clipBoard:React.createRef()
        };

    }

    downloadMedia(){
        const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const url = this.props.download_url;

        fetch(proxyurl + url, {
            method: 'GET',
            headers: {
                'Content-Type': this.props.media_type,
                'Access-Control-Allow-Origin':'*'
            },
        })
        .then((response) => response.blob())
        .then((blob) => {
            console.log(blob);
            const link = document.getElementById('forDownload');
            link.setAttribute(
            'download',
             url,
            );

            // Start download
            link.click();

        });
    }

    copyToClipboard(val) {
        const el = document.getElementById("forClipboard");
        //const el = this.state.clipBoard.current;
        el.value = val;
        el.setAttribute("readonly", "");
        el.style.position = "absolute";
        el.style.left = "-9999px";

        el.select();
        document.execCommand("copy");
        alert("Copied the text: " + el.value);
    }



    render() {      
        let button;
        if(this.props.media_type.toString() === "video"){
            this.setState.isVideo = true;
            button = <PlayButton />        }
        else {
            this.setState.isVideo = false;
            button = <Empty />
        }

        const invisibility = {
            visibility:'hidden'
        }

      return (
          <div className="item">    

                <img src={this.props.cover_photo_url} className="img-responsive item-image" alt="..." width="70" height="120"></img>
                <div className="media-buttons ui icon buttons">
                    <button className="ui button no-play copy" onClick={this.copyToClipboard.bind(this.props.tracking_link)}><i className="fas fa-link" aria-hidden="true"></i></button>
                    <button className="ui button no-play" onClick={this.downloadMedia.bind(this.props.download_url, this.props.media_type)}><i className="fas fa-download" aria-hidden="true"
                        data-toggle="tooltip" data-placement="top" title="Will download only Firebase url"
                    ></i></button>                   
                    { button }
                </div>
                <a href={this.state.linkUrl} id="forDownload" style={invisibility}></a> 
                <textarea id="forClipboard"  onChange="return false"  style={invisibility} ref={this.state.clipBoard}></textarea> 
          </div>
        
     
        )
    }
  }
  export default Mediaitem;
